Trigger.script:
meld.rs = {}
meld.center = false
meld.ub = {}
meld.rooms_in_circle = {}
meld.paint()

Alias.script:
if not next(meld.rs) then
  mm.echof("You don't even have a meld up... make one first, silly.")
  return
end

local f = mm.getDefaultColor()
mm.echof("My meld:")
decho(string.format("%s  rooms total:           %d\n", f, table.size(meld.rs)))
decho(string.format("%s  unbreakable rooms:     %d\n", f, table.size(meld.ub)))
decho(string.format("%s  breakable endpoints:   %d%s\n", f, table.size(meld.br),
(function()
  if not next(meld.br) then return "" end
  local t = {}
  for room in pairs(meld.br) do t[#t+1] = room end
  return " - "..table.concat(t, ", ")
end)()))

decho(string.format("%s  rooms in circles:      %d%s\n", f, table.size(meld.rooms_in_circle),
(function()
  if not next(meld.rooms_in_circle) then return "" end
  local t = {}
  for room in pairs(meld.rooms_in_circle) do t[#t+1] = room end
  return " - "..table.concat(t, ", ")
end)()

))

Script.script:
function mm_register_meld() mm.conf.meldtracker = type(mm.conf.meldtracker) ~= 'nil' and mm.conf.meldtracker or true mm.config.setoption("meldtracker", { type = "boolean", vconfig2string = true, onshow = function (defaultcolour) fg("gold") echoLink("meldtracker: ", "", "m&m Meld Tracker", true) fg(defaultcolour) echo("Visual plotting ") fg("a_cyan") echoLink(mm.conf.meldtracker and "enabled" or "disabled", "mm.config.set('meldtracker', "..(mm.conf.meldtracker and 'false' or 'true')..", true)", 'Click to '..(mm.conf.meldtracker and 'disable' or 'enable')..' displaying the meld', true) fg(defaultcolour) echo(".\n") end, onenabled = function() meld.paint() mm.echof("<0,250,0>Will%s plot your meld on the map visually.", mm.getDefaultColor()) end, ondisabled = function() mm.echof("<250,0,0>Won't%s plot your meld on the map visually.", mm.getDefaultColor())
for i = 1, #meld.rooms_painted do unHighlightRoom(meld.rooms_painted[i]) end meld.rooms_painted = {} end })
meld = mm.meld end
